mapper.insert:

  select LAST_INSERT_ID()

insert into tb_user_role (user_open_id, role_id, common_status, 
  create_time, create_user, create_user_name, 
  update_time, update_user, update_user_name
  )
values (#{userOpenId,jdbcType=VARCHAR}, #{roleId,jdbcType=INTEGER}, #{commonStatus,jdbcType=TINYINT}, 
  #{createTime,jdbcType=TIMESTAMP}, #{createUser,jdbcType=VARCHAR}, #{createUserName,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}, #{updateUserName,jdbcType=VARCHAR}
  )

mapper.select:
SELECT

  ${returnFields}




FROM tb_user_role

    AND id = #{id,jdbcType=BIGINT}

        #{item,jdbcType=BIGINT}

    AND user_open_id = #{userOpenId,jdbcType=VARCHAR}
    AND user_open_id LIKE concat(#{userOpenIdLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND role_id = #{roleId,jdbcType=INTEGER}

        #{item,jdbcType=INTEGER}

    AND common_status = #{commonStatus,jdbcType=TINYINT}

        #{item,jdbcType=TINYINT}

    AND create_time >= #{createTimeFrom,jdbcType=TIMESTAMP}
    AND create_time <= #{createTimeTo,jdbcType=TIMESTAMP}
    AND create_user = #{createUser,jdbcType=VARCHAR}
    AND create_user LIKE concat(#{createUserLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND create_user_name = #{createUserName,jdbcType=VARCHAR}
    AND create_user_name LIKE concat(#{createUserNameLike,jdbcType=VARCHAR},'%')
    AND update_time >= #{updateTimeFrom,jdbcType=TIMESTAMP}
    AND update_time <= #{updateTimeTo,jdbcType=TIMESTAMP}
    AND update_user = #{updateUser,jdbcType=VARCHAR}
    AND update_user LIKE concat(#{updateUserLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}

    AND update_user_name = #{updateUserName,jdbcType=VARCHAR}
    AND update_user_name LIKE concat(#{updateUserNameLike,jdbcType=VARCHAR},'%')

        #{item,jdbcType=VARCHAR}



    order by ${orderBy} ${order}
    order by id desc
